declare var _spPageContextInfo: {
  webAbsoluteUrl: string; // Absolútna URL aktuálneho webu
  userId: number; // ID aktuálne prihláseného používateľa
  webTitle: string; // Názov aktuálneho webu
  webServerRelativeUrl: string; // Server relatívna URL aktuálneho webu
  siteAbsoluteUrl: string; // Absolútna URL aktuálneho site kolekcie
  siteServerRelativeUrl: string; // Server relatívna URL aktuálneho site kolekcie
  tenantAppVersion: string; // Verzia tenant aplikácie
  isAppWeb: boolean; // True, ak je aktuálny web app web
  layoutsUrl: string; // Relatívna cesta k _layouts adresáru
  webLogoUrl: string; // URL loga webu
  webLanguage: number; // Jazykový kód aktuálneho webu
  currentCultureName: string; // Názov aktuálnej kultúry (napr. "en-US")
  currentUICultureName: string; // Názov aktuálnej UI kultúry
};